mapper.select:

select 

,

from skf.skf2010_t_attached_file
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and shain_no = #{shainNo,jdbcType=VARCHAR}
  and appl_date = #{applDate,jdbcType=TIMESTAMP}
  and appl_no = #{applNo,jdbcType=VARCHAR}
  and attached_name = #{attachedName,jdbcType=VARCHAR}

mapper.delete:

delete from skf.skf2010_t_attached_file
where company_cd = #{companyCd,jdbcType=VARCHAR}
  and shain_no = #{shainNo,jdbcType=VARCHAR}
  and appl_date = #{applDate,jdbcType=TIMESTAMP}
  and appl_no = #{applNo,jdbcType=VARCHAR}
  and attached_name = #{attachedName,jdbcType=VARCHAR}

mapper.update:

update skf.skf2010_t_attached_file

  
    attached_no = #{attachedNo,jdbcType=VARCHAR},
  
  
    file_size = #{fileSize,jdbcType=VARCHAR},
  
  
    delete_flag = #{deleteFlag,jdbcType=VARCHAR},
  
  
    insert_date = #{insertDate,jdbcType=TIMESTAMP},
  
  
    insert_user_id = #{insertUserId,jdbcType=VARCHAR},
  
  
    insert_program_id = #{insertProgramId,jdbcType=VARCHAR},
  
  update_date = now(),
  
    update_user_id = #{updateUserId,jdbcType=VARCHAR},
  
  
    update_program_id = #{updateProgramId,jdbcType=VARCHAR},
  
  
    file_stream = #{fileStream,jdbcType=BINARY},
  

where   update_date = #{lastUpdateDate,jdbcType=TIMESTAMP} and  company_cd = #{companyCd,jdbcType=VARCHAR}
  and shain_no = #{shainNo,jdbcType=VARCHAR}
  and appl_date = #{applDate,jdbcType=TIMESTAMP}
  and appl_no = #{applNo,jdbcType=VARCHAR}
  and attached_name = #{attachedName,jdbcType=VARCHAR}


update skf.skf2010_t_attached_file
set attached_no = #{attachedNo,jdbcType=VARCHAR},
  file_size = #{fileSize,jdbcType=VARCHAR},
  delete_flag = #{deleteFlag,jdbcType=VARCHAR},
  insert_date = #{insertDate,jdbcType=TIMESTAMP},
  insert_user_id = #{insertUserId,jdbcType=VARCHAR},
  insert_program_id = #{insertProgramId,jdbcType=VARCHAR},
  update_date = now(),
  update_user_id = #{updateUserId,jdbcType=VARCHAR},
  update_program_id = #{updateProgramId,jdbcType=VARCHAR},
  file_stream = #{fileStream,jdbcType=BINARY}
where   update_date = #{lastUpdateDate,jdbcType=TIMESTAMP} and  company_cd = #{companyCd,jdbcType=VARCHAR}
  and shain_no = #{shainNo,jdbcType=VARCHAR}
  and appl_date = #{applDate,jdbcType=TIMESTAMP}
  and appl_no = #{applNo,jdbcType=VARCHAR}
  and attached_name = #{attachedName,jdbcType=VARCHAR}


update skf.skf2010_t_attached_file
set attached_no = #{attachedNo,jdbcType=VARCHAR},
  file_size = #{fileSize,jdbcType=VARCHAR},
  delete_flag = #{deleteFlag,jdbcType=VARCHAR},
  insert_date = #{insertDate,jdbcType=TIMESTAMP},
  insert_user_id = #{insertUserId,jdbcType=VARCHAR},
  insert_program_id = #{insertProgramId,jdbcType=VARCHAR},
  update_date = now(),
  update_user_id = #{updateUserId,jdbcType=VARCHAR},
  update_program_id = #{updateProgramId,jdbcType=VARCHAR}
where   update_date = #{lastUpdateDate,jdbcType=TIMESTAMP} and  company_cd = #{companyCd,jdbcType=VARCHAR}
  and shain_no = #{shainNo,jdbcType=VARCHAR}
  and appl_date = #{applDate,jdbcType=TIMESTAMP}
  and appl_no = #{applNo,jdbcType=VARCHAR}
  and attached_name = #{attachedName,jdbcType=VARCHAR}
