mapper.select:

select 

from skf.skf3010_m_shataku_room_bihin
where shataku_kanri_no = #{shatakuKanriNo,jdbcType=NUMERIC}
  and shataku_room_kanri_no = #{shatakuRoomKanriNo,jdbcType=NUMERIC}
  and bihin_cd = #{bihinCd,jdbcType=VARCHAR}

mapper.delete:

delete from skf.skf3010_m_shataku_room_bihin
where shataku_kanri_no = #{shatakuKanriNo,jdbcType=NUMERIC}
  and shataku_room_kanri_no = #{shatakuRoomKanriNo,jdbcType=NUMERIC}
  and bihin_cd = #{bihinCd,jdbcType=VARCHAR}

mapper.update:

update skf.skf3010_m_shataku_room_bihin
set bihin_status_kbn = #{bihinStatusKbn,jdbcType=VARCHAR},
  bihin_latest_status_kbn = #{bihinLatestStatusKbn,jdbcType=VARCHAR},
  delete_flag = #{deleteFlag,jdbcType=VARCHAR},
  insert_date = #{insertDate,jdbcType=TIMESTAMP},
  insert_user_id = #{insertUserId,jdbcType=VARCHAR},
  insert_program_id = #{insertProgramId,jdbcType=VARCHAR},
  update_date = now(),
  update_user_id = #{updateUserId,jdbcType=VARCHAR},
  update_program_id = #{updateProgramId,jdbcType=VARCHAR}
where   update_date = #{lastUpdateDate,jdbcType=TIMESTAMP} and  shataku_kanri_no = #{shatakuKanriNo,jdbcType=NUMERIC}
  and shataku_room_kanri_no = #{shatakuRoomKanriNo,jdbcType=NUMERIC}
  and bihin_cd = #{bihinCd,jdbcType=VARCHAR}
