mapper.select:

select 

from skf.skf3030_t_parking_rireki
where shataku_kanri_id = #{shatakuKanriId,jdbcType=NUMERIC}
  and parking_lend_no = #{parkingLendNo,jdbcType=NUMERIC}
  and year_month = #{yearMonth,jdbcType=VARCHAR}

mapper.delete:

delete from skf.skf3030_t_parking_rireki
where shataku_kanri_id = #{shatakuKanriId,jdbcType=NUMERIC}
  and parking_lend_no = #{parkingLendNo,jdbcType=NUMERIC}
  and year_month = #{yearMonth,jdbcType=VARCHAR}
