mapper.select:

select 

from skf.skf3030_t_shozoku_rireki
where shataku_kanri_id = #{shatakuKanriId,jdbcType=NUMERIC}
  and year_month = #{yearMonth,jdbcType=VARCHAR}

mapper.delete:

delete from skf.skf3030_t_shozoku_rireki
where shataku_kanri_id = #{shatakuKanriId,jdbcType=NUMERIC}
  and year_month = #{yearMonth,jdbcType=VARCHAR}
